context-param.param-value:
PREFIX acl: <http://www.w3.org/ns/auth/acl#>

SELECT  ?Type
WHERE
  {   { GRAPH $this
          { $this
                      a  ?Type
          }
      }
    UNION
      { GRAPH ?g
            { $this
                        a  <http://www.semanticdesktop.org/ontologies/2007/03/22/nfo#FileDataObject> ;
                        a  ?Type
            }
      }
  }

PREFIX  acl:  <http://www.w3.org/ns/auth/acl#>

SELECT  ?owner
WHERE
  { GRAPH $this
      { $this  acl:owner  ?owner }
  }

PREFIX  rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX  acl:  <http://www.w3.org/ns/auth/acl#>
PREFIX  foaf: <http://xmlns.com/foaf/0.1/>

DESCRIBE ?auth
WHERE
  {
    GRAPH ?g {
      { ?auth  acl:agent  $agent }
     UNION
       { ?auth   acl:agentGroup  ?Group

         GRAPH ?groupG {
           ?Group  foaf:member  $agent
         }
         FILTER(strstarts(str(?groupG), str($base)))

       }
     UNION
       { ?auth  acl:agentClass  foaf:Agent }
     UNION
       { ?auth  acl:agentClass  $AuthenticatedAgentClass }

       ?auth  acl:mode  ?Mode .

       { ?auth  acl:accessTo  $this }
     UNION
       {   { ?auth  acl:accessToClass  $Type }
         UNION
           { ?auth  acl:accessToClass  ?Class .
             GRAPH <urn:x-arq:UnionGraph> {
               $Type (rdfs:subClassOf)* ?Class
             }
           }
       }
    }

    FILTER(strstarts(str(?g), str($base)))
  }

PREFIX  rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX  acl:  <http://www.w3.org/ns/auth/acl#>
PREFIX  foaf: <http://xmlns.com/foaf/0.1/>

DESCRIBE ?auth
WHERE
  { GRAPH ?g
      {   { ?auth  acl:mode           acl:Control .
              { ?auth  acl:agent  $agent }
            UNION
              { ?auth   acl:agentGroup  ?Group

                GRAPH ?groupG {
                  ?Group  foaf:member  $agent
                }
                FILTER(strstarts(str(?groupG), str($base)))

              }
          }
        UNION
          { ?auth  acl:agentClass  $AuthenticatedAgentClass ;
                   acl:mode        ?Mode
              { ?auth  acl:accessTo  $this
              }
            UNION
              { ?auth  acl:accessToClass  $Type }
            UNION
              { ?auth  acl:accessToClass  ?Class .
                GRAPH <urn:x-arq:UnionGraph> {
                  $Type (rdfs:subClassOf)* ?Class
                }
              }
          }
        UNION
          { ?auth  acl:agentClass  foaf:Agent ;
                   acl:mode        acl:Read
              { ?auth  acl:accessTo  $this
              }
            UNION
              { ?auth  acl:accessToClass  $Type }
            UNION
              { ?auth  acl:accessToClass  ?Class .
                GRAPH <urn:x-arq:UnionGraph> {
                  $Type (rdfs:subClassOf)* ?Class
                }
              }
            # only namespace, signup, OAuth2 login and WebID profiles can be public in admin app, nothing else
            FILTER ( $this IN (uri(concat(str($base), "ns")), uri(concat(str($base), "sign%20up"))) || strstarts(str($this), concat(str($base), "acl/agents/")) || strstarts(str($this), concat(str($base), "acl/public-keys/")))
          }
      }

    FILTER(strstarts(str(?g), str($base)))
  }

PREFIX  foaf: <http://xmlns.com/foaf/0.1/>

SELECT ?Agent ?agentGraph
WHERE
  {
      { GRAPH ?agentGraph
          { ?Agent  foaf:mbox  $mbox }
      }
  }

PREFIX  lacl: <https://w3id.org/atomgraph/linkeddatahub/admin/acl#>
PREFIX  sioc: <http://rdfs.org/sioc/ns#>

DESCRIBE ?account ?agent
WHERE
  { GRAPH ?g
      { ?account  sioc:id          $id ;
                  lacl:issuer      $issuer ;
                  sioc:account_of  ?agent
      }
  }

PREFIX cert:    <http://www.w3.org/ns/auth/cert#>

SELECT *
{
   ?webid cert:key [
      cert:modulus ?mod ;
      cert:exponent ?exp
   ] .
}

PREFIX  owl:  <http://www.w3.org/2002/07/owl#>

CONSTRUCT 
  { 
    ?s ?p ?o .
  }
WHERE
  {   { GRAPH ?g
          { $ontology  a  owl:Ontology .
            ?s        ?p  ?o
          }
      }
    UNION
      { GRAPH $ontology
          { ?s  ?p  ?o }
      }
  }
