context-param.param-value:
PREFIX  rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX  acl:  <http://www.w3.org/ns/auth/acl#>
PREFIX  lacl: <https://w3id.org/atomgraph/linkeddatahub/admin/acl/domain#>
PREFIX  foaf: <http://xmlns.com/foaf/0.1/>
PREFIX  sioc: <http://rdfs.org/sioc/ns#>

DESCRIBE ?auth
FROM <urn:x-arq:UnionGraph>
WHERE
  {   { ?auth  acl:mode  ?Mode
          { ?auth  acl:accessTo  ?this }
        UNION
          {   { ?auth  acl:accessToClass  ?Type }
            UNION
              { ?auth  acl:accessToClass  ?Class .
                ?Type (rdfs:subClassOf)* ?Class
              }
            SERVICE ?endpoint
              { { GRAPH ?g
                    { ?this  a  ?Type }
                }
              }
          }
        {   { ?auth  acl:agent  ?agent }
          UNION
            { ?auth   acl:agentGroup  ?Group .
              ?Group  foaf:member     ?agent
            }
        }
      }
    UNION
      { ?auth  acl:mode  ?Mode
          { ?auth  acl:agentClass  foaf:Agent }
        UNION
          { ?auth  acl:agentClass  ?AuthenticatedAgentClass }
          { ?auth  acl:accessTo  ?this }
        UNION
          {   { ?auth  acl:accessToClass  ?Type }
            UNION
              { ?auth  acl:accessToClass  ?Class .
                ?Type (rdfs:subClassOf)* ?Class
              }
            SERVICE ?endpoint
              { { GRAPH ?g
                    { ?this  a  ?Type }
                }
              }
          }
      }
  }

PREFIX  rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX  acl:  <http://www.w3.org/ns/auth/acl#>
PREFIX  lacl: <https://w3id.org/atomgraph/linkeddatahub/admin/acl/domain#>
PREFIX  foaf: <http://xmlns.com/foaf/0.1/>
PREFIX  sioc: <http://rdfs.org/sioc/ns#>

DESCRIBE ?auth
FROM <urn:x-arq:UnionGraph>
WHERE
  {   { ?auth  acl:mode           acl:Control .
        ?doc   foaf:primaryTopic  ?auth
          { ?auth  acl:agent  ?agent }
        UNION
          { ?auth   acl:agentGroup  ?Group .
            ?Group  foaf:member     ?agent
          }
      }
    UNION
      { ?auth  acl:agentClass  ?AuthenticatedAgentClass ;
               acl:mode        ?Mode
          { ?auth  acl:accessTo  ?this
            OPTIONAL
              { ?this  a  ?Type }
          }
        UNION
          { ?this  a  ?Type
              { ?auth  acl:accessToClass  ?Type }
            UNION
              { ?auth  acl:accessToClass  ?Class .
                ?Type (rdfs:subClassOf)* ?Class
              }
          }
      }
    UNION
      { ?auth  acl:agentClass  foaf:Agent ;
               acl:mode        acl:Read
          { ?auth  acl:accessTo  ?this
            OPTIONAL
              { ?this  a  ?Type }
          }
        UNION
          { ?this  a  ?Type
              { ?auth  acl:accessToClass  ?Type }
            UNION
              { ?auth  acl:accessToClass  ?Class .
                ?Type (rdfs:subClassOf)* ?Class
              }
          }
      }
  }

PREFIX  lacl: <https://w3id.org/atomgraph/linkeddatahub/admin/acl/domain#>
PREFIX  sioc: <http://rdfs.org/sioc/ns#>

DESCRIBE ?account ?agent
WHERE
  { GRAPH ?g
      { ?account  sioc:id          ?id ;
                  lacl:issuer      ?issuer ;
                  sioc:account_of  ?agent
      }
  }

PREFIX cert:    <http://www.w3.org/ns/auth/cert#>

SELECT *
{
   ?webid cert:key [
      cert:modulus ?certMod ;
      cert:exponent ?exp
   ] .
   FILTER (?certMod = ?mod)
}

PREFIX  dct:  <http://purl.org/dc/terms/>
PREFIX  void: <http://rdfs.org/ns/void#>

INSERT {
  GRAPH ?graphGraphDoc {
    ?this dct:modified ?modified .
  }
}
WHERE
  { GRAPH ?graphGraphDoc
      { ?this  void:inDataset  ?graph ;
               dct:created     ?created
      }
    BIND(now() AS ?modified)
  }

PREFIX  void: <http://rdfs.org/ns/void#>

DELETE WHERE
{
  GRAPH ?graphGraphDoc {
    ?this void:inDataset ?graph .
    ?s ?p ?o .
  }
}
